package while_loop_task;

public class wl1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		while (i < 7) {
			System.out.println("hi" + " " + i);
			i++;
		}
		int c = 5;
		while (c >= 0) {
			System.out.println("hellow" + " " + c);
			c--;
		}
	}

}
